head.script:
function calc(){
    var url = "http://192.168.1.6/cgi-bin/twice.pl"; /* the URL of the server*/
	var obj = document.getElementById("display"); /*where the returned data is to be displayed */
	var val = document.getElementById("number").value; /* the captured INPUT data*/
	var ret = browser.transmitTextDataOverIP(url,val,"EUC-JP"); 
	/* HTTP POST API - returned value (array) is stored in the variable ret */
	if( ret[0] == 1 ){
		obj.firstChild.data = "The received data: "+ ret[2];
	}else{
		obj.firstChild.data = "ERROR: Failed communication.";
	}
}
