head.script:
	
		var video = document.getElementById("video");
		var fullScreenFlag=0;

/* list of videos to be displayed */

		var urlList = new Array();
		urlList[0] = "http://180.37.183.147/lime/resolveconf/hawaii-01.cpc";
		urlList[1] = "http://180.37.183.147/lime/resolveconf/hawaii-03.cpc";
		urlList[2] = "http://180.37.183.147/lime/resolveconf/hawaii-05.cpc";
		urlList[3] = "http://kawamori.github.com/LIME-Book/lime/resolveconf/notredame.cpc";
	
		


function focus(){ /* main function to get the id of the focused element <div>, to which the url of the video is associated */

    var target = document.currentEvent.target;  /* target element of the focus */
	var url = urlList[target.id]; /* the url of the video pointed by the focused element */
	

	
	video.data=url;
	video.streamStatus="play"; /*start the video */
	
}

function blur(){ /* action to be taken when unfocused */

		video.streamStatus="stop"; 
	}

function show(){ }

function fullScreen(){

if(fullScreenFlag==0){

browser.lockscreen();

video.normalStyle.left=0;
video.normalStyle.top=0;
video.normalStyle.width=960;
video.normalStyle.top=540;
browser.unlockscreen();
fullScreenFlag=1;
}else{

video.normalStyle.left=200;
video.normalStyle.top=0;
video.normalStyle.width=600;
video.normalStyle.top=500;
fullScreenFlag=0;

}


 }
function goback(){ browser.launchDocument("../../../index.xml", "cut");	}
