head.script:
		var urlList = new Array();
		urlList[0] = "video/entrance.html";
		urlList[1] = "video/speech.html";
		urlList[2] = "";
		urlList[3] = "";
		urlList[4] = "";
		urlList[5] = "";
		urlList[6] = "";
		urlList[7] = "";
		urlList[8] = "";
		urlList[9] = "";
		urlList[10] = "";
		urlList[11] = "";
		urlList[12] = "";
		

//var base = "http://localhost/";
//var base = "http://127.0.0.1/";
var base = "";

function jump(){
	var url = urlList[document.currentEvent.target.id]
	
		browser.launchDocument(base + url);
}



function focus(){
	var target = document.currentEvent.target;
	target.nextSibling.normalStyle.visibility = "visible";
}

function blur(){
	var target = document.currentEvent.target;
	target.nextSibling.normalStyle.visibility = "hidden";
	target.nextSibling.nextSibling.normalStyle.visibility = "hidden";
	}

function show(){
	var target = document.currentEvent.target;
	target.nextSibling.normalStyle.visibility = "visible";
}


function goback(){ browser.launchDocument("../index.xml", "cut");	}
