head.script:
var Index = 0;
var image ="who-banner0.png";
var dir = "./";


function keydown(){
	var code = document.currentEvent.keyCode;
	var prev = Index;

	if( code == 3 ){
		if(--Index < 0) {Index = 5;}
	}
	else if( code == 4){	
		if(++Index > 5) {Index = 0;}
	}
	var next = Index;
	if( prev != next ){	
	document.getElementById("b"+0).data = dir+"who-banner"+next+".jpg";
  /*  document.getElementById("b"+0).data = dir+"dengue"+prev+".png";*/
	}
}
