head.script:
var Pref = new Array(
 "Hokkaido","Aomori","Iwate","Miyagi","Akita","Yamagata","Fkushima","썸모쮦","퀡모퍸","쒀뫊",
 "Saitama","Chiba","Tokyo","Kanagawa","찡모","퇓모집","첋모첥","퉈모썭","집모퐾","컃모펉",
 "없모퇣","첀모쒋","쌵모","짏모쫜","쨕모쓃","모쾂","췮모쥔","퉢모쟮","匣뫑","쒩집",
 "컅모쩓","쾒모줣","쒋모집","졼모쾒","집모젛","퀘모쾒","좨모첥","쌵모","좩모","퉈모쒋",
 "줪모쓃","컃모쥙","읺모","췮모퉓","왚모쥙","쨢徽","쒌모퀿"
 );

function start()
{
	var str = "";
	var ret = browser.readPersistentArray("nvram://receiverinfo/prefecture", "U:1B");
	
	if( ret != null && 0 <= ret[0] && ret[0] < 50 )
	{
		var pid = ret[0];
		if( pid == 0) pid = 1;		// 쾗퍕뱭직쀎퍕뱭
		else if( pid == 48) pid = 13;	// 쾗쾒퇪뭙쾗쾂
		else if( pid == 49) pid = 46;	// 쨢徽芼貌集쀂싵徽

		pid--;
		str = "You domicile is "+Pref[pid]+".";
	}
	else{
		str = "Prefecture information is not registered.";
	}

	ret = browser.readPersistentArray("nvram://receiverinfo/zipcode", "S:7B");
	if( ret != null && ret[0] != "" ){
		str += "Your Zipcode is" +ret[0]+".";
	}else{
		str += "Zipcode is not registered.";
	}

	document.getElementById("status").firstChild.data = str;
}

function goback(){ browser.launchDocument("../index.xml", "cut");	}
